		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: sessions_before_delete
	if(function_exists('sessions_before_delete')){
		$args=array();
		if(!sessions_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	// child table: students
	$res = sql("select `id` from `sessions` where `id`='$selected_id'", $eo);
	$id = db_fetch_row($res);
