		$RetMsg = str_replace("<Cancel>", "<input type=\"button\" class=\"button\" value=\"".$Translation['no']."\" onClick=\"window.location='sessions_view.php?SelectedID=".urlencode($selected_id)."';\">", $RetMsg);
