function sessions_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('sessions');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='sessions' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='sessions' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['Year'] = makeSafe($_REQUEST['Year']);
		if($data['Year'] == empty_lookup_value){ $data['Year'] = ''; }
	if($data['Year']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Year': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['Term'] = makeSafe($_REQUEST['Term']);
		if($data['Term'] == empty_lookup_value){ $data['Term'] = ''; }
	if($data['Term']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Term': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['status'] = makeSafe($_REQUEST['status']);
		if($data['status'] == empty_lookup_value){ $data['status'] = ''; }
	$data['selectedID']=makeSafe($selected_id);

	// hook: sessions_before_update
	if(function_exists('sessions_before_update')){
		$args=array();
		if(!sessions_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `sessions` set       `Year`=' . (($data['Year'] !== '' && $data['Year'] !== NULL) ? "'{$data['Year']}'" : 'NULL') . ', `Term`=' . (($data['Term'] !== '' && $data['Term'] !== NULL) ? "'{$data['Term']}'" : 'NULL') . ', `status`=' . (($data['status'] !== '' && $data['status'] !== NULL) ? "'{$data['status']}'" : 'NULL') . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="sessions_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: sessions_after_update
	if(function_exists('sessions_after_update')){
		$res = sql("SELECT * FROM `sessions` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!sessions_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='sessions' and pkValue='".makeSafe($selected_id)."'", $eo);

}

